import {Component, HostBinding, OnInit} from '@angular/core';

@Component( {
  selector: 'ngx-dialog-backdrop',
  template: '',
  host: {
    'class': 'ngx-dialog-backdrop'
  }
} )
export class NgxDialogBackdrop implements OnInit {
  private shown = false;

  @HostBinding( "class.shown" ) get fadeIn() {
    return this.shown;
  }

  ngOnInit() {
    setTimeout( ()=> {
      this.shown = true;
    }, 20 );
  }
}
