import {
  ApplicationRef,
  Injectable,
  Injector,
  ReflectiveInjector,
  ComponentFactory,
  ComponentFactoryResolver,
  ComponentRef,
  TemplateRef
} from '@angular/core';

import {ContentRef} from '../util/popup';
import {isDefined, isString} from '../util/util';

import {NgxDialogBackdrop} from './dialog-backdrop';
import {NgxDialogWindow} from './dialog-window';

import {NgxActiveDialog, NgxDialogRef} from './dialog-ref';

@Injectable()
export class NgxDialogStack {
  private _backdropFactory: ComponentFactory<NgxDialogBackdrop>;
  private _windowFactory: ComponentFactory<NgxDialogWindow>;

  constructor(
      private _applicationRef: ApplicationRef, private _injector: Injector,
      private _componentFactoryResolver: ComponentFactoryResolver) {
    this._backdropFactory = _componentFactoryResolver.resolveComponentFactory(NgxDialogBackdrop);
    this._windowFactory = _componentFactoryResolver.resolveComponentFactory(NgxDialogWindow);
  }

  open(moduleCFR: ComponentFactoryResolver, contentInjector: Injector, content: any, options:any): NgxDialogRef {
    const containerSelector = options.container || 'body';
    const containerEl = document.querySelector(containerSelector);

    if (!containerEl) {
      throw new Error(`The specified modal container "${containerSelector}" was not found in the DOM.`);
    }

    const activeDialog = new NgxActiveDialog();
    const contentRef = this._getContentRef(moduleCFR, options.injector || contentInjector, content, activeDialog);

    if( options.data ) {
      for( let name in options.data ) {
        contentRef.componentRef.instance[name] = options.data[ name ];
      }
    }

    let windowCmptRef: ComponentRef<NgxDialogWindow>;
    let backdropCmptRef: ComponentRef<NgxDialogBackdrop>;
    let ngxDialogRef: NgxDialogRef;


    if (options.backdrop !== false) {
      backdropCmptRef = this._backdropFactory.create(this._injector);
      this._applicationRef.attachView(backdropCmptRef.hostView);
      containerEl.appendChild(backdropCmptRef.location.nativeElement);
    }
    windowCmptRef = this._windowFactory.create(this._injector, contentRef.nodes);
    this._applicationRef.attachView(windowCmptRef.hostView);
    containerEl.appendChild(windowCmptRef.location.nativeElement);

    ngxDialogRef = new NgxDialogRef(windowCmptRef, contentRef, backdropCmptRef);

    activeDialog.close = (result: any) => { ngxDialogRef.close(result); };
    activeDialog.dismiss = (reason: any) => { ngxDialogRef.dismiss(reason); };

    this._applyWindowOptions(windowCmptRef.instance, options);

    return ngxDialogRef;
  }

  private _applyWindowOptions(windowInstance: NgxDialogWindow, options: Object): void {
    ['backdrop', 'keyboard', 'size', 'windowClass'].forEach((optionName: string) => {
      if (isDefined(options[optionName])) {
        windowInstance[optionName] = options[optionName];
      }
    });
  }

  private _getContentRef(
      moduleCFR: ComponentFactoryResolver, contentInjector: Injector, content: any,
      context: NgxActiveDialog): ContentRef {
    if (!content) {
      return new ContentRef([]);
    } else if (content instanceof TemplateRef) {
      const viewRef = content.createEmbeddedView(context);
      this._applicationRef.attachView(viewRef);
      return new ContentRef([viewRef.rootNodes], viewRef);
    } else if (isString(content)) {
      return new ContentRef([[document.createTextNode(`${content}`)]]);
    } else {
      const contentCmptFactory = moduleCFR.resolveComponentFactory(content);
      const modalContentInjector =
          ReflectiveInjector.resolveAndCreate([{provide: NgxActiveDialog, useValue: context}], contentInjector);
      const componentRef = contentCmptFactory.create(modalContentInjector);
      this._applicationRef.attachView(componentRef.hostView);
      return new ContentRef([[componentRef.location.nativeElement]], componentRef.hostView, componentRef);
    }
  }
}
