import {NgModule, ModuleWithProviders}       from '@angular/core';
import {NgxDialogBackdrop}                   from './dialog-backdrop';
import {NgxDialogWindow}                     from './dialog-window';
import {NgxDialogStack}                      from './dialog-stack';
import {NgxDialogService}                    from './dialog.service';
export {NgxDialogService, NgxDialogOptions}  from './dialog.service';
export {NgxDialogRef, NgxActiveDialog}       from './dialog-ref';

@NgModule({
  declarations: [NgxDialogBackdrop, NgxDialogWindow],
  entryComponents: [NgxDialogBackdrop, NgxDialogWindow],
  providers: [NgxDialogService]
} )
export class NgxDialogModule {
  static forRoot(): ModuleWithProviders { return {ngModule: NgxDialogModule, providers: [NgxDialogService, NgxDialogStack]}; }
}
